import '../styles/App.css';
import React, { Component } from 'react';


import Youtube from '../apis/Youtube';
import ApiService from '../apis/ApiService'; //SpringBootÏÑúÎ≤Ñ Ïó∞Í≤∞ API

//ÌôîÎ©¥ component
import SearchBar from './SearchBar';
import VideoList from './VideoList';
import VideoDetail from './VideoDetail';
import SaveList from './SaveList';



class App extends Component {
  constructor(props) {
    super(props);
    this.state = { //setStateÎ©îÏÑúÎìúÎ°ú Í∞í Î≥ÄÍ≤ΩÍ∞ÄÎä•!
      videos : [],//Í≤ÄÏÉâÎêú Í≤∞Í≥ºÍ∞Ä Ï†ÄÏû•ÎêòÎäî Î∞∞Ïó¥
      slectedVideo: null,
      favoriteVideos : [] //Ï¶êÍ≤®Ï∞æÍ∏∞Ïö© Î∞∞Ïó¥
    }       
  }

  handleSubmit = async(term) => {
    const res = await Youtube.get('/search', {
      params: { q : term }
    });
    //debugger;
    this.setState({
      videos: res.data.items
    });
  }
  //videoItemÏóêÏÑú Ï†ÑÎã¨Îêú ÎπÑÎîîÏò§ Í∞ùÏ≤¥Î•º SelectedVideoÎ°ú Ìï†Îãπ. 
  //SeletedVideoÎäî VideoDetailÎ°ú Ï†ÑÎã¨
  handleVideoSelect = (video) => {
    //debugger;
    this.setState({ slectedVideo: video}); // Í∞í ÎÑ£Ïùå
    console.log('‚≠ê',this.state.slectedVideo);//Î°úÍ≥†Î°ú ÏΩòÏÜîÍµ¨Î≥Ñ

  }
  handleFavoriteSelect = (video) => {
 //   debugger; ÌÅ¥Î¶≠Ìïº Îïå Í∞íÏù¥ ÎÑòÏñ¥Ïò§ÎäîÍ∞Ä
    console.log('üéà',video);
    this.setState({ slectedVideo: video });

  }
  //VideoItem -> VideoList -> AppÏ≤òÎ¶¨
  handleVideoSave = async(video) => {
//  alert('ÎèôÏòÅÏÉÅ Ï†ÄÏû•!');
    var temp = {
      video_id_videoId : video.id.videoId, 
      video_snippet_title : video.snippet.title, 
      video_snippet_description : video.snippet.description};

      console.log(temp); //Ï†ÄÏû•ÏùÑ ÎàåÎ†ÄÏùÑ Îïå Îç∞Ïù¥ÌÑ∞ ÌëúÏãú
      //DBÏ†ÄÏû•
      await ApiService.addMovie(temp)
      .then(res => {
        this.reloadFavoriteMovies();
      })
      .catch(err => {
        console.error("ApiService.addMovie() ÏóêÎü¨", err);
        alert("Ï¶êÍ≤®Ï∞æÍ∏∞ Î™©Î°ù Ï∂îÍ∞Ä Ïò§Î•òü§¶‚Äç‚ôÄÔ∏è\n Í¥ÄÎ¶¨ÏûêÏóêÍ≤å Î¨∏ÏùòÌïòÏÑ∏Ïöî");
      });

  }

  componentDidMount() {//ÎùºÏù¥ÌîÑ ÏÇ¨Ïù¥ÌÅ¥ Ïù¥Î≤§Ìä∏, renderÌõÑ Ïã§Ìñâ
    console.info("componentDidMount");
    this.reloadFavoriteMovies(); //Î©îÏÑúÎìú Ïã§Ìñâ
  }

  reloadFavoriteMovies = async() => {//ÎπÑÎèôÍ∏∞
    await ApiService.fetchMovies()
      .then(res => {
//      debugger;
        let temps = res.data;
        var i = 0;
        var fvl = [];
        while ( i < temps.length ) {
          //TODO : 
          fvl.push({
            idx: temps[i].id,
            id: { 
              kind: "youtube#video", 
              videoId: temps[i].video_id_videoId},
            snippet: 
              {title: temps[i].video_snippet_title, 
              description: temps[i].video_snippet_description }
            });
          i += 1;
        }
        console.log(fvl);
        this.setState({favoriteVideos: fvl })
      })
      .catch(err => {
        console.error("ApiService.fetchMovies() ÏóêÎü¨", err);
        alert("Ï¶êÍ≤®Ï∞æÍ∏∞ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïò§Î•òü§¶‚Äç‚ôÄÔ∏è\n Í¥ÄÎ¶¨ÏûêÏóêÍ≤å Î¨∏ÏùòÌïòÏÑ∏Ïöî");
      });
  }

  render() {
    console.info('App render()');
    return (
      <div className='App container show-grid'>
        <div className='row'>
          <div className='col'>
            {/* Í≤ÄÏÉâÎ∞î jsÏ£ºÏÑù */}
            <SearchBar
              handleFormSubmit={this.handleSubmit}></SearchBar>
            <div className='row pt-3'>
              {/* pt :1~5 ÎÑìÏù¥ Ï°∞Ï†à */}
              <div className='col-8' style={{backgroundColor:'whitesmoke'}}>
                {/* Ïú†ÌäúÎ∏å ÌîåÎ†àÏù¥Ïñ¥ */}
                <VideoDetail
                  video={this.state.slectedVideo} />
                {/* Ï†ÄÏû• Î¶¨Ïä§Ìä∏  */}
                <SaveList 
                  videos={this.state.favoriteVideos} 
                  handleFavoriteSelect={this.handleFavoriteSelect}
                  handleFavoriteDelete={this.handleFavoriteDelete}/>

              </div>
              <div className='col-4'>
                {/* Í≤ÄÏÉâÍ≤∞Í≥º */}
                <VideoList 
                  videos={this.state.videos}
                  handleVideoSelect = {this.handleVideoSelect}
                  handleVideoSave= {this.handleVideoSave}>  
                </VideoList>
              </div>
            </div>
          </div>
        </div>

      </div>
    );
  }
}

export default App;
