import React, { Component } from 'react';
import * as Icons from 'react-bootstrap-icons';

class SearchBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
        term: 'Input search words'
    }
  }

  handleChange = (e) => {

  }

  handleSubmit = (e) => {
      
  }

  render() {
    console.info('SearchBar render()');
    return (
      <div className='container'>
        <div className='row'>
          <div className='col'>
            <form className='card card-sm' style={{margin:'10px'}}>
              <div className='card-body row align-items-center '>
                <div className='col-auto'>
                  <Icons.Search />
                </div>
                <div className='col'>
                  <input
                    type='search'
                    name='youtube-search'
                    placeholder={this.state.term} 
                    className='form-control form-control-lg form-control-borderless'
                    style={{border:'0'}}
                  />
                </div>
                <div className='col-auto'>
                  <button type='submit' className='btn btn-sm btn-outline-info'>
                    Search
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default SearchBar;
