import '../styles/App.css';
import React, { Component } from 'react';

import Youtube from '../apis/Youtube';
import SearchBar from './SearchBar';
import VideoList from './VideoList';
import VideoDetail from './VideoDetail';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = { //setState메서드로 값 변경가능!
      videos : [],//검색된 결과가 저장되는 배열
      slectedVideo: null
    }       
  }

  handleSubmit = async(term) => {
    const res = await Youtube.get('/search', {
      params: { q : term }
    });
    //debugger;
    this.setState({
      videos: res.data.items
    });
  }
  //videoItem에서 전달된 비디오 객체를 SelectedVideo로 할당. 
  //SeletedVideo는 VideoDetail로 전달
  handleVideoSelect = (video) => {
    //debugger;
    this.setState({ slectedVideo: video}); // 값 넣음
    console.log('⭐',this.state.slectedVideo);//로고로 콘솔구별

  }

  render() {
    console.info('App render()');
    return (
      <div className='App container show-grid' style ={{marginTop:'20px'}}>
        <div className='row'>
          <div className='col'>
            <SearchBar
              handleFormSubmit={this.handleSubmit}></SearchBar>
            <div className='row' style ={{margin:'20px'}} >
              <div className='col-8' style={{backgroundColor:'lightyellow'}}>
                <VideoDetail />

              </div>
              <div className='col-4'>
                <VideoList 
                  videos={this.state.videos}
                  handleVideoSelect={this.handleVideoSelect}></VideoList>
              </div>
            </div>
          </div>
        </div>

      </div>
    );
  }
}

export default App;
