import '../styles/App.css';
import React, { Component } from 'react';

import Youtube from '../apis/Youtube';
import SearchBar from './SearchBar';
import VideoList from './VideoList';
import VideoItem from './VideoItem';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      videos : []
    }       
  }

  handleSubmit = async(term) => {
    const res = await Youtube.get('/search', {
      params: { q : term }
    });
    //debugger;
    this.setState({
      videos: res.data.items
    });
  }

  render() {
    console.info('App render()');
    return (
      <div className='App container show-grid' style ={{marginTop:'20px'}}>
        <div className='row'>
          <div className='col'>
            <SearchBar
              handleFormSubmit={this.handleSubmit}></SearchBar>
            <div className='row' style ={{margin:'20px'}} >
              <div className='col-8' style={{backgroundColor:'lightyellow' }}>
                <VideoItem></VideoItem>
              </div>
              <div className='col-4' style={{backgroundColor:'lightBlue'}}>
                <VideoList 
                  videos={this.state.videos}></VideoList>
              </div>
            </div>
          </div>
        </div>

      </div>
    );
  }
}

export default App;
