import '../styles/App.css';
import React, { Component } from 'react';

import Youtube from '../apis/Youtube';
import SearchBar from './SearchBar';
import VideoList from './VideoList';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      videos : [{id:1},{id:2},{id:'yaho'}]
    }


  }

  handleSubmit = async(term) => {
    const res = Youtube.get('/search', {
      params: { q : term }
    });

    this.setState({
      videos: res.data.items
    });
  }

  render() {
    console.info('App render()');
    return (
      <div className='App container show-grid' style ={{marginTop:'20px'}}>
        <div className='row'>
          <div className='col'>
            <SearchBar
              handleSubmit={this.handleSubmit}></SearchBar>
            <div className='row'>
              <div className='col-8' style={{backgroundColor:'lightyellow'}}>
                유튜브 플레이 위치

              </div>
              <div className='col-4' style={{backgroundColor:'lightBlue'}}>
                <VideoList 
                  videos={this.state.videos}></VideoList>

              </div>
            </div>
          </div>
        </div>

      </div>
    );
  }
}

export default App;
